import React, { useState } from 'react';
import { useForm } from "react-hook-form";
import { Link, useNavigate } from "react-router-dom";
import axios from 'axios';
import API_BACK from '../API_BACK';

function Login() {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const onSubmit = async (data) => {
    setLoading(true);
    setError(null);
    
    try {
      const response = await axios.post(`${API_BACK}//`, data);
      
      // Si la connexion réussit
      localStorage.setItem('token', response.data.token);
      navigate('/dashboard'); // Redirection après login
      
    } catch (err) {
      setError(
        err.response?.data?.message || 
        err.message || 
        'Une erreur est survenue'
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container d-flex flex-column" style={{ maxWidth: '400px' }}>
      <form onSubmit={handleSubmit(onSubmit)} className="d-flex flex-column border-0 p-3">
        <h2 className="text-center mb-4">Connexion</h2>
        
        {error && (
          <div className="alert alert-danger">
            {error}
          </div>
        )}

        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email</label>
          <input
            id="email"
            type="email"
            className={`form-control ${errors.email ? 'is-invalid' : ''}`}
            placeholder="votre@email.com"
            {...register("email", { 
              required: "Ce champ est obligatoire",
              pattern: {
                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                message: "Email invalide"
              }
            })}
          />
          {errors.email && (
            <div className="invalid-feedback">
              {errors.email.message}
            </div>
          )}
        </div>

        <div className="mb-3">
          <label htmlFor="password" className="form-label">Mot de passe</label>
          <input
            id="password"
            type="password"
            className={`form-control ${errors.password ? 'is-invalid' : ''}`}
            placeholder="••••••••"
            {...register("password", { 
              required: "Ce champ est obligatoire",
              minLength: {
                value: 6,
                message: "Minimum 6 caractères"
              }
            })}
          />
          {errors.password && (
            <div className="invalid-feedback">
              {errors.password.message}
            </div>
          )}
        </div>

        <button
          type="submit"
          className="btn btn-primary w-100 py-2"
          disabled={loading}
        >
          {loading ? (
            <>
              <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
              Chargement...
            </>
          ) : 'Se connecter'}
        </button>

        <div className="d-flex justify-content-between mt-3">
          <Link to="/prof/register" className="text-decoration-none">
            Inscription Professeur
          </Link>
          <Link to="/std/register" className="text-decoration-none">
            Inscription Étudiant
          </Link>
        </div>
      </form>
    </div>
  );
}

export default Login;
