import React from 'react'
import { Link, useNavigate } from "react-router-dom";
import { useForm } from 'react-hook-form'; 
export default function registerPROF() {
    const { register, handleSubmit, formState: { errors } } = useForm();

    const onSubmit = async (data) => {
        setLoading(true);
        setError(null);
        
        try {
          const response = await axios.post(`${API_BACK}/auth/login`, data, {
            headers: {
              'Content-Type': 'application/json',
            },
          });
          
          // Si la connexion réussit
        //   localStorage.setItem('token', response.data.token);
         // Redirection après login
         console.log(response)
          
        } catch (err) {
          setError(
            err.response?.data?.message || 
            err.message || 
            'Une erreur est survenue'
          );
        } finally {
          setLoading(false);
        }
      };
  return (
    <div className="container d-flex flex-column">
    <form onSubmit={handleSubmit(onSubmit)} className="d-flex flex-column align-items-center border-0">
        <p className="align-self-start fw-semibold text-primary">Register of Prof</p>
        
        <input 
           
            type="email"
            placeholder="Email"
            {...register("email", { 
                required: "Ce champ est obligatoire",
                pattern: {
                  value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                  message: "Email invalide"
                }
              })}
            className="form-control mt-3 border-secondary focus:border-danger focus:ring-1 focus:ring-danger"
        />
         <input 
            
            type="text"
            placeholder="Nom"
            {...register("nom", { 
                required: "Ce champ est obligatoire",
              })}
            className="form-control mt-3 border-secondary focus:border-danger focus:ring-1 focus:ring-danger"
        />
          <input 
           
            type="text"
            placeholder="Prenom"
            {...register("prenom", { 
                required: "Ce champ est obligatoire",
              })}
            className="form-control mt-3 border-secondary focus:border-danger focus:ring-1 focus:ring-danger"
        />
            <input 
            
            type="password"
            placeholder="Password"
            {...register("password", { 
                required: "Ce champ est obligatoire",
                minLength: {
                    value: 5,
                    message: "Minimum 5 caractères"
                  }
              })}
            className="form-control mt-3 border-secondary focus:border-danger focus:ring-1 focus:ring-danger"
        />
            <input 
        
            type="text"
            placeholder="Departement"
            {...register("prenom", { 
                required: "Ce champ est obligatoire",
              })}
            className="form-control mt-3 border-secondary focus:border-danger focus:ring-1 focus:ring-danger"
        />
        
        
    <button
            type="submit"
            className="btn btn-primary w-100 mt-3 mb-3 p-2"
        >
           Register
        </button>
        
        <Link to="/login" className="text-decoration-none">
            <p className="text-primary">Login</p>
        </Link>
    </form>
</div>
  )
}
